        static JavaUnityPlayerActivityWithANRWatchDogFileBuilder()
        {
            EditorApplication.delayCall += OnAfterDomainReload;
        }
        
        private static void OnAfterDomainReload()
        {
            if (EditorApplication.isPlayingOrWillChangePlaymode)
            {
                EditorApplication.delayCall -= OnAfterDomainReload;
                return;
            }
            var content = NonFireBaseContent;
#if GOOGLE_FIREBASE_MESSAGING
            content = FireBaseContent;
#endif
            _ = SharedEditorUtils.WriteProjectRelativeFile("Assets/Plugins/Android/UnityPlayerActivityWithANRWatchDog.java", content, silent: true);
            EditorApplication.delayCall -= OnAfterDomainReload;
#if ENABLE_SHARED_EDITOR_LOGGER
            UnityEngine.Debug.Log("âœ… JavaUnityPlayerActivityWithANRWatchDogFileBuilder Domain reload complete!");
#endif
        }
    }
